import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import { toast } from "react-toastify";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getCartTotal, clearCart } = useCart();
  const [showCheckout, setShowCheckout] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    payment: "",
  });
  const navigate = useNavigate();

  const handleQuantityChange = (productId, newQuantity) => {
    const quantity = parseInt(newQuantity) || 1;
    if (quantity < 1) {
      toast.error("Quantity must be at least 1");
      return;
    }
    updateQuantity(productId, quantity);
    toast.info("Cart updated!");
  };

  const handleCheckoutSubmit = (e) => {
    e.preventDefault();
    if (!formData.name || !formData.email || !formData.address || !formData.payment) {
      toast.error("Please fill in all checkout fields");
      return;
    }

    toast.success("Order placed successfully!");
    clearCart();
    setFormData({ name: "", email: "", address: "", payment: "" });
    setShowCheckout(false);

    setTimeout(() => {
      navigate("/order-success");
    }, 1500);
  };

  return (
    <div className="container py-5">
      <h2 className="mb-4">Your Cart</h2>

      {cart.length === 0 ? (
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="text-muted"
        >
          Your cart is empty.
        </motion.p>
      ) : (
        <>
          <AnimatePresence>
            {cart.map((item) => (
              <motion.div
                key={item._id}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 50 }}
                transition={{ duration: 0.3 }}
                className="d-flex justify-content-between align-items-center border p-3 rounded mb-2 shadow-sm"
              >
                <div>
                  <h5 className="mb-1">{item.name}</h5>
                  <p className="mb-1">Price: Rs. {item.price.toFixed(2)}</p>
                  <div className="d-flex align-items-center gap-2">
                    <button
                      onClick={() => handleQuantityChange(item._id, item.quantity - 1)}
                      className="btn btn-outline-secondary btn-sm"
                    >
                      −
                    </button>
                    <span>{item.quantity}</span>
                    <button
                      onClick={() => handleQuantityChange(item._id, item.quantity + 1)}
                      className="btn btn-outline-secondary btn-sm"
                    >
                      +
                    </button>
                  </div>
                </div>

                <div className="text-end">
                  <p className="mb-1">
                    Total: Rs. {(item.price * item.quantity).toFixed(2)}
                  </p>
                  <button
                    onClick={() => {
                      removeFromCart(item._id);
                      toast.warn("Item removed");
                    }}
                    className="btn btn-danger btn-sm"
                  >
                    Remove
                  </button>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>

          {/* Cart summary */}
          <div className="mt-4 p-3 border rounded shadow-sm">
            <h4>Cart Summary</h4>
            <p>Total: Rs. {getCartTotal().toFixed(2)}</p>
            <div className="d-flex gap-2">
              <button
                onClick={() => setShowCheckout(!showCheckout)}
                className="btn btn-primary"
              >
                {showCheckout ? "Cancel Checkout" : "Proceed to Checkout"}
              </button>
              <button
                onClick={() => {
                  clearCart();
                  toast.info("Cart cleared!");
                }}
                className="btn btn-outline-danger"
              >
                Clear Cart
              </button>
            </div>
          </div>

          {/* Checkout form */}
          {showCheckout && (
            <motion.form
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.4 }}
              onSubmit={handleCheckoutSubmit}
              className="mt-4 border p-4 rounded shadow-sm"
            >
              <h4 className="mb-3">Checkout</h4>
              <input
                type="text"
                placeholder="Full Name"
                className="form-control mb-2"
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              />
              <input
                type="email"
                placeholder="Email"
                className="form-control mb-2"
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              />
              <textarea
                placeholder="Address"
                className="form-control mb-2"
                value={formData.address}
                onChange={(e) => setFormData({ ...formData, address: e.target.value })}
              />
              <select
                className="form-control mb-3"
                value={formData.payment}
                onChange={(e) => setFormData({ ...formData, payment: e.target.value })}
              >
                <option value="">Select Payment Method</option>
                <option value="Credit Card">Credit Card</option>
                <option value="Cash on Delivery">Cash on Delivery</option>
              </select>
              <button type="submit" className="btn btn-success w-100">
                Place Order
              </button>
            </motion.form>
          )}
        </>
      )}
    </div>
  );
};

export default Cart;
