import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { productsAPI } from '../api/axios';
import ProductCard from '../components/ProductCard';
import Loader from '../components/Loader';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      setLoading(true);
      const response = await productsAPI.getAll();
      setFeaturedProducts(response.data.slice(0, 6));
      setError(null);
    } catch (err) {
      setError('Failed to load products. Please try again later.');
      console.error('Error fetching products:', err);
    } finally {
      setLoading(false);
    }
  };

  // Styles
  const styles = {
    container: { padding: '2rem 1rem', maxWidth: '1200px', margin: '0 auto' },
    hero: { textAlign: 'center', padding: '2rem 0' },
    heroH1: { fontSize: '2rem', color: '#2c3e50', marginBottom: '1rem' },
    heroP: { fontSize: '1rem', color: '#7f8c8d', marginBottom: '1.5rem' },
    btnPrimary: {
      fontSize: '1rem',
      padding: '10px 25px',
      cursor: 'pointer',
      backgroundColor: '#007bff',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
    },
    featuredSection: { marginTop: '2.5rem' },
    featuredH2: { fontSize: '1.8rem', color: '#2c3e50', marginBottom: '1.5rem', textAlign: 'center' },
    productsGrid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1.5rem' },
    noProducts: { textAlign: 'center', color: '#7f8c8d' },
    viewAll: { textAlign: 'center', marginTop: '2rem' },
    btnSuccess: {
      padding: '10px 25px',
      fontSize: '1rem',
      cursor: 'pointer',
      backgroundColor: '#28a745',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
    },
    // Responsive
    '@media(max-width:768px)': {
      heroH1: { fontSize: '1.5rem' },
      heroP: { fontSize: '0.95rem' },
      btnPrimary: { width: '100%', fontSize: '0.95rem' },
      btnSuccess: { width: '100%', fontSize: '0.95rem' },
    },
    '@media(max-width:480px)': {
      productsGrid: { gridTemplateColumns: '1fr' },
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.hero}>
        <h1 style={styles.heroH1}>Welcome to Our Store</h1>
        <p style={styles.heroP}>
          Effortless style and timeless comfort. Discover our new arrivals crafted for the modern individual.
        </p>
        <Link to="/products">
          <button style={styles.btnPrimary}>Shop Now</button>
        </Link>
      </div>

      <div style={styles.featuredSection}>
        <h2 style={styles.featuredH2}>Featured Products</h2>

        {loading && <Loader />}
        {error && <div className="error-message">{error}</div>}
        {!loading && !error && featuredProducts.length === 0 && (
          <p style={styles.noProducts}>No products available at the moment.</p>
        )}

        {!loading && !error && featuredProducts.length > 0 && (
          <div style={styles.productsGrid}>
            {featuredProducts.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
        )}
      </div>

      <div style={styles.viewAll}>
        <Link to="/products">
          <button style={styles.btnSuccess}>View All Products</button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
