import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getCartTotal, clearCart, user } = useCart();
  const navigate = useNavigate();

  const handleCheckout = () => {
    if (!user) {
      alert('Please login to proceed with checkout');
      navigate('/login');
      return;
    }
    alert('Checkout functionality would be implemented here');
    // In a real app, this would navigate to a checkout page
  };

  const handleQuantityChange = (productId, newQuantity) => {
    const quantity = parseInt(newQuantity) || 1;
    updateQuantity(productId, quantity);
  };

  if (cart.length === 0) {
    return (
      <div className="container" style={{ paddingTop: '3rem', textAlign: 'center' }}>
        <h2 style={{ fontSize: '2rem', marginBottom: '1rem', color: '#2c3e50' }}>
          Your Cart is Empty
        </h2>
        <p style={{ color: '#7f8c8d', marginBottom: '2rem' }}>
          Add some products to your cart to see them here.
        </p>
        <img src = "data:image/png;base64," alt="Empty Cart" style={{
            width: "280px",
            marginBottom: "2rem",
            borderRadius: "20px",
            boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
            alignContent: 'center',
          }} />
       
        <Link to="/products">
          <button className="btn btn-primary"  style={{ 
      backgroundColor: "#3498db", 
      color: "white", 
      padding: "10px 20px", 
      border: "none", 
      borderRadius: "6px", 
      cursor: "pointer", 
      fontSize: "1rem",
      marginLeft: "-200px"
    }}>
            Continue Shopping
          </button>
        </Link>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <div className="container">
        <h1 style={{ fontSize: '2.5rem', marginBottom: '2rem', color: '#2c3e50' }}>
          Shopping Cart
        </h1>

        <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '2rem' }}>
          <div>
            {cart.map((item) => (
              <div key={item._id} className="cart-item">
                <div className="cart-item-info">
                  <img
                    src={item.image || 'https://via.placeholder.com/80?text=No+Image'}
                    alt={item.name}
                    className="cart-item-image"
                  />
                  <div>
                    <h3 style={{ fontSize: '1.2rem', marginBottom: '0.5rem', color: '#2c3e50' }}>
                      {item.name}
                    </h3>
                    <p style={{ color: '#27ae60', fontWeight: 'bold', fontSize: '1.1rem' }}>
                      Rs{item.price.toFixed(2)}
                    </p>
                  </div>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                    <label style={{ fontWeight: '500' }}>Qty:</label>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item._id, e.target.value)}
                      style={{
                        width: '70px',
                        padding: '5px',
                        border: '1px solid #ddd',
                        borderRadius: '5px',
                        textAlign: 'center',
                      }}
                    />
                  </div>
                  <p style={{ fontWeight: 'bold', minWidth: '80px', textAlign: 'right' }}>
                    Rs{(item.price * item.quantity).toFixed(2)}
                  </p>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    className="btn btn-danger"
                    style={{ padding: '8px 15px' }}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}

            <div style={{ marginTop: '1rem' }}>
              <button
                onClick={clearCart}
                className="btn"
                style={{ background: '#95a5a6', color: 'white', padding: '10px 20px' }}
              >
                Clear Cart
              </button>
            </div>
          </div>

          <div>
            <div className="cart-summary">
              <h2 style={{ fontSize: '1.5rem', marginBottom: '1.5rem', color: '#2c3e50' }}>
                Order Summary
              </h2>
              <div style={{ marginBottom: '1rem' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                  <span>Subtotal:</span>
                  <span>Rs{getCartTotal().toFixed(2)}</span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                  <span>Shipping:</span>
                  <span>Rs0.00</span>
                </div>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                  <span>Tax:</span>
                  <span>Rs0.00</span>
                </div>
                <hr style={{ margin: '1rem 0', border: 'none', borderTop: '1px solid #ddd' }} />
                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '1.3rem', fontWeight: 'bold' }}>
                  <span>Total:</span>
                  <span style={{ color: '#27ae60' }}>Rs{getCartTotal().toFixed(2)}</span>
                </div>
              </div>
              <button
                onClick={handleCheckout}
                className="btn btn-success"
                style={{ width: '100%', padding: '12px', fontSize: '1.1rem', marginTop: '1rem' }}
              >
                Proceed to Checkout
              </button>
              <Link to="/products">
                <button
                  className="btn btn-primary"
                  style={{ width: '100%', padding: '12px', marginTop: '1rem' }}
                >
                  Continue Shopping
                </button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
