import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { motion, AnimatePresence } from "framer-motion";

toast.configure();

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getCartTotal, clearCart, user } = useCart();
  const navigate = useNavigate();
  const [showCheckout, setShowCheckout] = useState(false);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [step, setStep] = useState(1); // 1 = cart, 2 = shipping, 3 = confirmation
  const [shippingInfo, setShippingInfo] = useState({
    fullName: "",
    email: "",
    phone: "",
    address: "",
    city: "",
    zipCode: "",
    country: "Sri Lanka",
  });

  const handleCheckout = () => {
    if (!user) {
      toast.error("Please login to proceed with checkout");
      navigate("/login");
      return;
    }
    setShowCheckout(true);
    setStep(2);
  };

  const handleQuantityChange = (productId, newQuantity) => {
    const quantity = parseInt(newQuantity) || 1;
    if (quantity < 1) {
      toast.warning("Quantity must be at least 1");
      return;
    }
    updateQuantity(productId, quantity);
    toast.info("Cart updated!");
  };

  const handleInputChange = (e) => {
    setShippingInfo({
      ...shippingInfo,
      [e.target.name]: e.target.value,
    });
  };

  const handlePlaceOrder = () => {
    if (
      !shippingInfo.fullName ||
      !shippingInfo.email ||
      !shippingInfo.phone ||
      !shippingInfo.address ||
      !shippingInfo.city ||
      !shippingInfo.zipCode
    ) {
      toast.error("Please fill in all required fields");
      return;
    }

    const orderDetails = {
      items: cart,
      total: getCartTotal(),
      shippingInfo: shippingInfo,
      orderDate: new Date().toISOString(),
      orderId: "ORD-" + Math.random().toString(36).substr(2, 9).toUpperCase(),
    };

    console.log("Order placed:", orderDetails);
    toast.success(`Order placed successfully! Order ID: ${orderDetails.orderId}`);
    setOrderPlaced(true);
    setStep(3);

    setTimeout(() => {
      clearCart();
      setShowCheckout(false);
      setOrderPlaced(false);
      setStep(1);
      setShippingInfo({
        fullName: "",
        email: "",
        phone: "",
        address: "",
        city: "",
        zipCode: "",
        country: "Sri Lanka",
      });
      navigate("/products");
    }, 4000);
  };

  // Progress Indicator
  const ProgressBar = () => (
    <div style={{ display: "flex", justifyContent: "center", marginBottom: "20px" }}>
      {["Cart", "Shipping", "Confirmation"].map((label, index) => (
        <div key={index} style={{ flex: 1, textAlign: "center" }}>
          <div
            style={{
              height: "10px",
              borderRadius: "5px",
              backgroundColor: step >= index + 1 ? "#27ae60" : "#ccc",
              margin: "0 5px",
            }}
          />
          <p style={{ fontSize: "0.9rem", marginTop: "5px" }}>{label}</p>
        </div>
      ))}
    </div>
  );

  // Order Success View
  if (orderPlaced) {
    return (
      <motion.div
        className="container"
        style={{
          paddingTop: "3rem",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          minHeight: "60vh",
          textAlign: "center",
        }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <ProgressBar />
        <div
          style={{
            backgroundColor: "#d4edda",
            border: "1px solid #c3e6cb",
            borderRadius: "10px",
            padding: "30px",
            marginBottom: "20px",
            maxWidth: "500px",
          }}
        >
          <div style={{ fontSize: "60px", marginBottom: "20px" }}>✓</div>
          <h2 style={{ color: "#155724", marginBottom: "10px" }}>
            Order Placed Successfully!
          </h2>
          <p style={{ color: "#155724" }}>
            Thank you for your purchase. Your order has been confirmed.
          </p>
        </div>
        <p style={{ color: "#7f8c8d" }}>Redirecting to shop...</p>
      </motion.div>
    );
  }

  // Checkout Form View
  if (showCheckout) {
    return (
      <motion.div
        className="container"
        style={{ paddingTop: "2rem", maxWidth: "800px" }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <ProgressBar />
        <h1 style={{ fontSize: "2.5rem", marginBottom: "2rem", color: "#2c3e50" }}>
          Checkout
        </h1>

        {/* Shipping Form */}
        <div
          style={{
            backgroundColor: "white",
            padding: "20px",
            borderRadius: "10px",
            border: "1px solid #dee2e6",
          }}
        >
          <h3 style={{ marginBottom: "20px", color: "#2c3e50" }}>
            Shipping Information
          </h3>

          {/* Name */}
          <div style={{ marginBottom: "15px" }}>
            <label style={{ display: "block", marginBottom: "5px", fontWeight: "500" }}>
              Full Name *
            </label>
            <input
              type="text"
              name="fullName"
              value={shippingInfo.fullName}
              onChange={handleInputChange}
              placeholder="John Doe"
              style={{
                width: "100%",
                padding: "10px",
                border: "1px solid #ddd",
                borderRadius: "5px",
              }}
            />
          </div>

          {/* Email + Phone */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr",
              gap: "15px",
              marginBottom: "15px",
            }}
          >
            <div>
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={shippingInfo.email}
                onChange={handleInputChange}
                placeholder="you@example.com"
                style={{ width: "100%", padding: "10px", border: "1px solid #ddd" }}
              />
            </div>
            <div>
              <label>Phone *</label>
              <input
                type="tel"
                name="phone"
                value={shippingInfo.phone}
                onChange={handleInputChange}
                placeholder="+94 71 234 5678"
                style={{ width: "100%", padding: "10px", border: "1px solid #ddd" }}
              />
            </div>
          </div>

          {/* Address */}
          <div style={{ marginBottom: "15px" }}>
            <label>Address *</label>
            <input
              type="text"
              name="address"
              value={shippingInfo.address}
              onChange={handleInputChange}
              placeholder="123, Main Street"
              style={{ width: "100%", padding: "10px", border: "1px solid #ddd" }}
            />
          </div>

          {/* City + Zip */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "1fr 1fr 1fr",
              gap: "15px",
              marginBottom: "20px",
            }}
          >
            <div>
              <label>City *</label>
              <input
                type="text"
                name="city"
                value={shippingInfo.city}
                onChange={handleInputChange}
                placeholder="Colombo"
                style={{ width: "100%", padding: "10px", border: "1px solid #ddd" }}
              />
            </div>
            <div>
              <label>Zip Code *</label>
              <input
                type="text"
                name="zipCode"
                value={shippingInfo.zipCode}
                onChange={handleInputChange}
                placeholder="10000"
                style={{ width: "100%", padding: "10px", border: "1px solid #ddd" }}
              />
            </div>
            <div>
              <label>Country</label>
              <input
                type="text"
                name="country"
                value={shippingInfo.country}
                readOnly
                style={{
                  width: "100%",
                  padding: "10px",
                  border: "1px solid #ddd",
                  background: "#f8f9fa",
                }}
              />
            </div>
          </div>

          <div style={{ display: "flex", gap: "10px" }}>
            <button
              onClick={() => setShowCheckout(false)}
              style={{
                flex: 1,
                padding: "12px",
                backgroundColor: "#6c757d",
                color: "white",
                border: "none",
                borderRadius: "5px",
              }}
            >
              Back to Cart
            </button>
            <button
              onClick={handlePlaceOrder}
              style={{
                flex: 1,
                padding: "12px",
                backgroundColor: "#27ae60",
                color: "white",
                border: "none",
                borderRadius: "5px",
              }}
            >
              Place Order
            </button>
          </div>
        </div>
      </motion.div>
    );
  }

  // Empty Cart
  if (cart.length === 0) {
    return (
      <motion.div
        className="container"
        style={{
          paddingTop: "3rem",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          minHeight: "60vh",
        }}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <h2 style={{ marginBottom: "1rem", color: "#2c3e50" }}>Your Cart is Empty</h2>
        <img
          src="https://cdn-icons-png.flaticon.com/512/263/263142.png"
          alt="Empty Cart"
          style={{ width: "200px", marginBottom: "20px" }}
        />
        <Link to="/products">
          <button className="btn btn-primary">Continue Shopping</button>
        </Link>
      </motion.div>
    );
  }

  // Cart View
  return (
    <div className="container" style={{ paddingTop: "2rem" }}>
      <ProgressBar />
      <h1 style={{ fontSize: "2.5rem", marginBottom: "2rem", color: "#2c3e50" }}>
        Shopping Cart
      </h1>

      <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: "2rem" }}>
        {/* Cart Items */}
        <div>
          <AnimatePresence>
            {cart.map((item) => (
              <motion.div
                key={item._id}
                initial={{ opacity: 0, x: -50 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 50 }}
                transition={{ duration: 0.3 }}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  marginBottom: "15px",
                  padding: "10px",
                  border: "1px solid #ddd",
                  borderRadius: "8px",
                }}
              >
                <div style={{ display: "flex", alignItems: "center", gap: "15px" }}>
                  <img
                    src={item.image || "https://via.placeholder.com/80"}
                    alt={item.name}
                    style={{ width: "80px", borderRadius: "8px" }}
                  />
                  <div>
                    <h3>{item.name}</h3>
                    <p style={{ color: "#27ae60", fontWeight: "bold" }}>
                      Rs{item.price.toFixed(2)}
                    </p>
                  </div>
                </div>

                {/* Quantity Stepper */}
                <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
                  <button onClick={() => handleQuantityChange(item._id, item.quantity - 1)}> - </button>
                  <span>{item.quantity}</span>
                  <button onClick={() => handleQuantityChange(item._id, item.quantity + 1)}> + </button>
                </div>

                <p style={{ fontWeight: "bold" }}>
                  Rs{(item.price * item.quantity).toFixed(2)}
                </p>

                <button
                  onClick={() => removeFromCart(item._id)}
                  className="btn btn-danger"
                >
                  Remove
                </button>
              </motion.div>
            ))}
          </AnimatePresence>

          <button onClick={clearCart} className="btn btn-secondary">
            Clear Cart
          </button>
        </div>

        {/* Summary */}
        <div>
          <div
            style={{
              border: "1px solid #ddd",
              padding: "20px",
              borderRadius: "8px",
            }}
          >
            <h2>Order Summary</h2>
            <p>Subtotal: Rs{getCartTotal().toFixed(2)}</p>
            <p>Shipping: Rs0.00</p>
            <p>Tax: Rs0.00</p>
            <h3>Total: Rs{getCartTotal().toFixed(2)}</h3>
            <button onClick={handleCheckout} className="btn btn-success" style={{ width: "100%" }}>
              Proceed to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
