import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const { cart, removeFromCart, updateQuantity, getCartTotal, clearCart, user } = useCart();
  const navigate = useNavigate();
  const [showCheckout, setShowCheckout] = useState(false);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [shippingInfo, setShippingInfo] = useState({
    fullName: '', email: '', phone: '', address: '', city: '', zipCode: '', country: 'Sri Lanka'
  });

  useEffect(() => {
    const handleResize = () => setWindowWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleCheckout = () => {
    if (!user) {
      alert('Please login to proceed with checkout');
      navigate('/login');
      return;
    }
    setShowCheckout(true);
  };

  const handleQuantityChange = (productId, newQuantity) => {
    const quantity = parseInt(newQuantity) || 1;
    updateQuantity(productId, quantity);
  };

  const handleInputChange = (e) => setShippingInfo({ ...shippingInfo, [e.target.name]: e.target.value });

  const handlePlaceOrder = () => {
    if (!shippingInfo.fullName || !shippingInfo.email || !shippingInfo.phone || !shippingInfo.address || !shippingInfo.city || !shippingInfo.zipCode) {
      alert('Please fill in all required fields');
      return;
    }

    const orderDetails = {
      items: cart,
      total: getCartTotal(),
      shippingInfo: shippingInfo,
      orderDate: new Date().toISOString(),
      orderId: 'ORD-' + Math.random().toString(36).substr(2, 9).toUpperCase()
    };

    console.log('Order placed:', orderDetails);
    alert(`Order placed successfully! Order ID: ${orderDetails.orderId}`);
    setOrderPlaced(true);

    setTimeout(() => {
      clearCart();
      setShowCheckout(false);
      setOrderPlaced(false);
      setShippingInfo({
        fullName: '', email: '', phone: '', address: '', city: '', zipCode: '', country: 'Sri Lanka'
      });
      navigate('/products');
    }, 5000);
  };

  const containerStyle = { padding: '2rem 1rem', maxWidth: '1200px', margin: '0 auto' };
  const gridStyle = {
    display: 'grid',
    gridTemplateColumns: windowWidth < 768 ? '1fr' : '2fr 1fr',
    gap: '2rem'
  };

  if (orderPlaced) {
    return (
      <div style={{ ...containerStyle, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '60vh', textAlign: 'center' }}>
        <div style={{ backgroundColor: '#d4edda', border: '1px solid #c3e6cb', borderRadius: '10px', padding: '30px', marginBottom: '20px', maxWidth: '500px' }}>
          <div style={{ fontSize: '60px', marginBottom: '20px' }}>âœ“</div>
          <h2 style={{ color: '#155724', marginBottom: '10px' }}>Order Placed Successfully!</h2>
          <p style={{ color: '#155724' }}>Thank you for your purchase. Your order has been confirmed.</p>
        </div>
        <p style={{ color: '#7f8c8d' }}>Redirecting to shop...</p>
      </div>
    );
  }

  if (showCheckout) {
    return (
      <div style={{ ...containerStyle, maxWidth: '800px' }}>
        <h1 style={{ fontSize: '2rem', marginBottom: '2rem', color: '#2c3e50' }}>Checkout</h1>
        <div style={{ display: 'grid', gap: '2rem' }}>
          <div style={{ backgroundColor: '#f8f9fa', padding: '20px', borderRadius: '10px', border: '1px solid #dee2e6' }}>
            <h3 style={{ marginBottom: '15px', color: '#2c3e50' }}>Order Summary</h3>
            {cart.map((item) => (
              <div key={item._id} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px', paddingBottom: '10px', borderBottom: '1px solid #dee2e6' }}>
                <span>{item.name} x {item.quantity}</span>
                <span style={{ fontWeight: 'bold' }}>Rs{(item.price * item.quantity).toFixed(2)}</span>
              </div>
            ))}
            <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '1.2rem', fontWeight: 'bold', marginTop: '15px', color: '#27ae60' }}>
              <span>Total:</span>
              <span>Rs{getCartTotal().toFixed(2)}</span>
            </div>
          </div>

          <div style={{ backgroundColor: 'white', padding: '20px', borderRadius: '10px', border: '1px solid #dee2e6' }}>
            <h3 style={{ marginBottom: '20px', color: '#2c3e50' }}>Shipping Information</h3>
            {['fullName', 'email', 'phone', 'address', 'city', 'zipCode'].map((field) => (
              <div key={field} style={{ marginBottom: '15px' }}>
                <label style={{ display: 'block', marginBottom: '5px', fontWeight: '500' }}>{field.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())} *</label>
                <input
                  type={field==='email'?'email':'text'}
                  name={field}
                  value={shippingInfo[field]}
                  onChange={handleInputChange}
                  style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px', fontSize: '1rem', boxSizing: 'border-box' }}
                />
              </div>
            ))}
            <div style={{ marginBottom: '20px' }}>
              <label>Country</label>
              <input type="text" name="country" value={shippingInfo.country} readOnly
                style={{ width: '100%', padding: '10px', border: '1px solid #ddd', borderRadius: '5px', fontSize: '1rem', backgroundColor: '#f8f9fa', boxSizing: 'border-box' }}
              />
            </div>
            <div style={{ display: 'flex', flexDirection: windowWidth<480?'column':'row', gap: '10px' }}>
              <button onClick={() => setShowCheckout(false)} style={{ flex: 1, padding: '12px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '5px', fontSize: '1rem', cursor: 'pointer', fontWeight: '500' }}>Back to Cart</button>
              <button onClick={handlePlaceOrder} style={{ flex: 1, padding: '12px', backgroundColor: '#28a745', color: 'white', border: 'none', borderRadius: '5px', fontSize: '1rem', cursor: 'pointer', fontWeight: '500' }}>Place Order</button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (cart.length === 0) {
    return (
      <div style={{ ...containerStyle, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '60vh', textAlign: 'center' }}>
        <h2 style={{ fontSize: '2rem', marginBottom: '1rem', color: '#2c3e50' }}>Your Cart is Empty</h2>
        <p style={{ color: '#7f8c8d', marginBottom: '2rem' }}>Add some products to your cart to see them here.</p>
        <img src="https://cdn-icons-png.flaticon.com/512/263/263142.png" alt="Empty Cart" style={{ width: '280px', marginBottom: '2rem', borderRadius: '20px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)' }} />
        <Link to="/products"><button style={{ backgroundColor: "#3498db", color: "white", padding: "12px 30px", border: "none", borderRadius: "6px", cursor: "pointer", fontSize: "1rem", fontWeight: "500" }}>Continue Shopping</button></Link>
      </div>
    );
  }

  return (
    <div style={containerStyle}>
      <h1 style={{ fontSize: '2rem', marginBottom: '2rem', color: '#2c3e50' }}>Shopping Cart</h1>
      <div style={gridStyle}>
        <div>
          {cart.map((item) => (
            <div key={item._id} style={{ display: 'flex', flexDirection: windowWidth<480?'column':'row', justifyContent: 'space-between', alignItems:'center', marginBottom: '1rem', padding: '10px', border: '1px solid #ddd', borderRadius: '10px' }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: windowWidth<480?'10px':'0' }}>
                <img src={item.image || 'https://via.placeholder.com/80?text=No+Image'} alt={item.name} style={{ width:'80px', height:'80px', borderRadius:'5px', objectFit:'cover' }} />
                <div>
                  <h3 style={{ fontSize: '1.1rem', marginBottom:'0.5rem' }}>{item.name}</h3>
                  <p style={{ fontWeight:'bold', color:'#27ae60' }}>Rs{item.price.toFixed(2)}</p>
                </div>
              </div>
              <div style={{ display:'flex', alignItems:'center', gap:'10px' }}>
                <input type="number" min="1" value={item.quantity} onChange={(e)=>handleQuantityChange(item._id, e.target.value)}
                  style={{ width:'60px', padding:'5px', border:'1px solid #ddd', borderRadius:'5px', textAlign:'center' }}
                />
                <p style={{ fontWeight:'bold', minWidth:'80px', textAlign:'right' }}>Rs{(item.price*item.quantity).toFixed(2)}</p>
                <button onClick={()=>removeFromCart(item._id)} style={{ padding:'8px 15px', backgroundColor:'#e74c3c', color:'white', border:'none', borderRadius:'5px', cursor:'pointer' }}>Remove</button>
              </div>
            </div>
          ))}
          <button onClick={clearCart} style={{ background:'#95a5a6', color:'white', padding:'10px 20px', border:'none', borderRadius:'5px', cursor:'pointer' }}>Clear Cart</button>
        </div>

        <div>
          <div style={{ padding:'20px', border:'1px solid #ddd', borderRadius:'10px', backgroundColor:'#f8f9fa' }}>
            <h2 style={{ marginBottom:'1rem', color:'#2c3e50' }}>Cart Summary</h2>    
            <p style={{ fontSize:'1.2rem', marginBottom:'1rem' }}>Total: <span style={{ fontWeight:'bold', color:'#27ae60' }}>Rs{getCartTotal().toFixed(2)}</span></p>
            <button onClick={handleCheckout} style={{ width:'100%', padding:'12px', backgroundColor:'#28a745', color:'white', border:'none', borderRadius:'5px', fontSize:'1rem', cursor:'pointer', fontWeight:'500' }}>Proceed to Checkout</button>
          </div>
        </div>
      </div>
    </div>
  );
}
