import React, { useEffect, useState } from "react";

import ProductCard from "../components/ProductCard";
import { productsAPI } from "../api/axios";
import { categoryImages } from "../assets/categoryImages";

const categories = [
  { label: "Men", value: "Men" },
  { label: "Women", value: "Women" },
  { label: "Accessories", value: "Accessories" },
];

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [active, setActive] = useState("all");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const res = await productsAPI.getAll();
      setProducts(res.data);
      setFiltered(res.data);
      setError(null);
    } catch (err) {
      setError("Failed to load products. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const handleCategory = (cat) => {
    setActive(cat);
    if (cat === "all") {
      setFiltered(products);
    } else {
      setFiltered(products.filter((p) => (p.category || "").toLowerCase() === cat.toLowerCase()));
    }
  };

  return (
    <div className="container" style={{ minHeight: "80vh", padding: "2rem 0" }}>
      <h1 style={{ textAlign: "center", fontSize: "2.5rem", marginBottom: "2rem", color: "#2c3e50" }}>
        Shop by Category
      </h1>

      {/* Category Cards */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "2.5rem",
          marginBottom: "3rem",
          flexWrap: "wrap",
        }}
      >
        {categories.map((cat) => (
          <div
            key={cat.value}
            onClick={() => handleCategory(cat.value)}
            style={{
              width: 260,
              height: 520,
              borderRadius: 20,
              background: active === cat.value ? "#111" : "#aaa",
              boxShadow: active === cat.value ? "0 8px 32px #6366f155" : "0 2px 8px #6366f122",
              cursor: "pointer",
              position: "relative",
              overflow: "hidden",
              display: "flex",
              alignItems: "flex-end",
              justifyContent: "center",
              transition: "all 0.25s cubic-bezier(.4,2,.6,1)",
              border: active === cat.value ? "3px solid #6366f1" : "3px solid transparent",
            }}
          >
            <img
              src={categoryImages[cat.label]}
              alt={cat.label}
              style={{
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                objectFit: "cover",
                opacity: active === cat.value ? 0.7 : 0.5,
                zIndex: 1,
                transition: "opacity 0.2s",
              }}
            />
            <div
              style={{
                position: "relative",
                zIndex: 2,
                width: "100%",
                textAlign: "center",
                paddingBottom: 32,
              }}
            >
              <span
                style={{
                  color: "#fff",
                  fontWeight: 700,
                  fontSize: 28,
                  letterSpacing: 2,
                  textShadow: "0 2px 8px #000a",
                  borderBottom: active === cat.value ? "3px solid #fff" : "3px solid transparent",
                  paddingBottom: 4,
                  transition: "border-bottom 0.2s",
                }}
              >
                {cat.label.toUpperCase()}
              </span>
            </div>
          </div>
        ))}
        {/* All category button */}
        <div
          onClick={() => handleCategory("all")}
          style={{
            width: 260,
            height: 520,
            borderRadius: 20,
            background: active === "all" ? "#6366f1" : "#eee",
            boxShadow: active === "all" ? "0 8px 32px #6366f155" : "0 2px 8px #6366f122",
            cursor: "pointer",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            border: active === "all" ? "3px solid #6366f1" : "3px solid transparent",
            fontWeight: 700,
            fontSize: 28,
            color: active === "all" ? "#fff" : "#6366f1",
            letterSpacing: 2,
            transition: "all 0.25s cubic-bezier(.4,2,.6,1)",
          }}
        >
          ALL
        </div>
      </div>

      {loading && <div style={{ textAlign: "center" }}>Loading products...</div>}
      {error && <div style={{ color: "red", textAlign: "center" }}>{error}</div>}

      {!loading && !error && (
        <div
          className="products-grid"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(260px, 1fr))",
            gap: "2rem",
            marginTop: "1.5rem",
          }}
        >
          {filtered.length === 0 ? (
            <div style={{ gridColumn: "1/-1", textAlign: "center", color: "#7f8c8d" }}>
              No products found in this category.
            </div>
          ) : (
            filtered.map((product) => <ProductCard key={product._id} product={product} />)
          )}
        </div>
      )}
    </div>
  );
};

export default Products;
