import React, { useEffect, useState } from "react";
import ProductCard from "../components/ProductCard";
import { productsAPI } from "../api/axios";

const categories = [
  { label: "All", value: "all" },
  { label: "Men", value: "Men" },
  { label: "Women", value: "Women" },
  { label: "Accessories", value: "Accessories" },
];

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [active, setActive] = useState("all");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const res = await productsAPI.getAll();
      setProducts(res.data);
      setFiltered(res.data);
      setError(null);
    } catch (err) {
      setError("Failed to load products. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const handleCategory = (cat) => {
    setActive(cat);
    if (cat === "all") {
      setFiltered(products);
    } else {
      setFiltered(products.filter((p) => (p.category || "").toLowerCase() === cat.toLowerCase()));
    }
  };

  return (
    <div className="container" style={{ minHeight: "80vh", padding: "2rem 0" }}>
      <h1 style={{ textAlign: "center", fontSize: "2.5rem", marginBottom: "2rem", color: "#2c3e50" }}>
        Explore Our Products
      </h1>

      <div style={{ display: "flex", justifyContent: "center", gap: "1.5rem", marginBottom: "2.5rem" }}>
        {categories.map((cat) => (
          <button
            key={cat.value}
            onClick={() => handleCategory(cat.value)}
            className={active === cat.value ? "btn btn-primary" : "btn btn-outline-primary"}
            style={{
              fontWeight: 600,
              fontSize: "1.1rem",
              borderRadius: "30px",
              padding: "10px 32px",
              boxShadow: active === cat.value ? "0 4px 16px #6366f133" : "none",
              transform: active === cat.value ? "scale(1.08)" : "scale(1)",
              transition: "all 0.2s cubic-bezier(.4,2,.6,1)",
              background: active === cat.value ? "#6366f1" : "#fff",
              color: active === cat.value ? "#fff" : "#6366f1",
              border: active === cat.value ? "2px solid #6366f1" : "2px solid #6366f1",
              cursor: "pointer",
            }}
          >
            {cat.label}
          </button>
        ))}
      </div>

      {loading && <div style={{ textAlign: "center" }}>Loading products...</div>}
      {error && <div style={{ color: "red", textAlign: "center" }}>{error}</div>}

      {!loading && !error && (
        <div
          className="products-grid"
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(260px, 1fr))",
            gap: "2rem",
            marginTop: "1.5rem",
          }}
        >
          {filtered.length === 0 ? (
            <div style={{ gridColumn: "1/-1", textAlign: "center", color: "#7f8c8d" }}>
              No products found in this category.
            </div>
          ) : (
            filtered.map((product) => <ProductCard key={product._id} product={product} />)
          )}
        </div>
      )}
    </div>
  );
};

export default Products;
