import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { productsAPI } from '../api/axios';
import ProductCard from '../components/ProductCard';
import Loader from '../components/Loader';

const Home = () => {
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchFeaturedProducts();
  }, []);

  const fetchFeaturedProducts = async () => {
    try {
      setLoading(true);
      const response = await productsAPI.getAll();
  // Get first 6 products as featured
  setFeaturedProducts(response.data.slice(0, 6));
      setError(null);
    } catch (err) {
      setError('Failed to load products. Please try again later.');
      console.error('Error fetching products:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div style={{ textAlign: 'center', padding: '3rem 0' }}>
        <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem', color: '#2c3e50' }}>
          Welcome to Our Store
        </h1>
        <p style={{ fontSize: '1.2rem', color: '#7f8c8d', marginBottom: '2rem' }}>
          Effortless style and timeless comfort. Discover our new arrivals crafted for the modern individual.
        </p>
        <Link to="/products">
          <button className="btn btn-primary" style={{ fontSize: '1.1rem', padding: '12px 30px' }}>
            Shop Now
          </button>
        </Link>
      </div>

      <div style={{ marginTop: '3rem' }}>
        <h2 style={{ fontSize: '2rem', marginBottom: '2rem', color: '#2c3e50' }}>
          Featured Products
        </h2>

        {loading && <Loader />}

        {error && <div className="error-message">{error}</div>}

        {!loading && !error && featuredProducts.length === 0 && (
          <p style={{ textAlign: 'center', color: '#7f8c8d' }}>No products available at the moment.</p>
        )}

        {!loading && !error && featuredProducts.length > 0 && (
          <div className="products-grid">
            {featuredProducts.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>
        )}
      </div>

      <div style={{ textAlign: 'center', marginTop: '3rem' }}>
        <Link to="/products">
          <button className="btn btn-success" style={{ padding: '12px 30px' }}>
            View All Products
          </button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
